import Stories from '../../containers/Stories';
import Loading from '../../components/Loading';

import Posts from '../../containers/Posts';

import './FeedRoute.scss';

const FeedRoute = () => {
  const users = [
    {"id":"1","name":"T'Challa","avatar":"https://viniciusvinna.netlify.app/assets//api-instagram/profiles/black-panther/black-panther-profile.jpg","username":"blackpanther","email":"blackpanther@gmail.com"},
    {"id":"2","name":"Bruce Wayne","avatar":"https://viniciusvinna.netlify.app/assets//api-instagram/profiles/bruce/bruce-profile.jpg","username":"brucewayne","email":"iamnotthebatman@hotmail.com"},
    {"id":"3","name":"Carol Danvers","avatar":"https://viniciusvinna.netlify.app/assets//api-instagram/profiles/carol/carol-profile.jpg","username":"captainmarvel","email":"captainmarvel@yahoo.com"}
  ]

  const stories = [{"id":"1","userId":"1","videoUrl":"https://viniciusvinna.netlify.app/assets/api-instagram/profiles/black-panther/black-panther-stories.mp4"},
  {"id":"2","userId":"2","videoUrl":"https://viniciusvinna.netlify.app/assets/api-instagram/profiles/bruce/bruce-stories.mp4"},
  {"id":"3","userId":"5","videoUrl":"https://viniciusvinna.netlify.app/assets/api-instagram/profiles/gamora/gamora-stories.mp4"}]

  const getUserHandler = (userId) => {
    console.log(userId);
    return users.filter((user) => user.id === userId);
  } 

  return (
    <div>
      <Stories stories={stories} getUserHandler={getUserHandler} />
    </div>
  );
};

export default FeedRoute;
