import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import './Post.scss';

const Post = ({ postInfo, userInfo }) => {
  const [following, toggleFolowing] = useState(false);
  const [like, toggleLike] = useState(false);

  const {comments, id, imageUrl, likes, userId} = postInfo;
  const {avatar, email, name, username} = userInfo;

  const isLiked = !like ? "s" : "";

  return (
    <article className="post">
      <header className="post__header">
        <div className="user">
          <Link to={`/user/${username}`} className="user__thumb">
            <img src={avatar} alt={name} />
          </Link>
          <Link to={`/user/${username}`} className="user__name">
            {name}
          </Link>
        </div>
        <button onClick={() => toggleFolowing(!following)} className="post__context">
          <span className="follow-btn">Seguir</span>
          <span className={"follow-btn" + (!following ? " is-following" : "")}>
            {!following ? "Seguindo" : "Seguir"}
          </span>
        </button>
        <figure className="post__figure">
            <img src={imageUrl} alt={username} />
        </figure>
      </header>
      <nav className="post__controls">
        <button onClick={() => toggleLike(!like)} className="post__control">
          <span className="far fa-heart"></span>
        </button>
        <div className="post__status">
          <div className="user">
            <span>
              curtido por <Link to="/">{comments.name}</Link>{` e outra${isLiked}`}<Link to="/"> {} {` pessoa${isLiked}`}</Link>
            </span>
          </div>
        </div>
      </nav>

    </article>
  );
};

export default Post;
