import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import './Post.scss';

const Post = ({ postInfo, userInfo }) => {
  const [following, toggleFolowing] = useState(false);
  const [] = useState(false);

  const {comments, id, imageUrl, likes, userId} = postInfo;
  const {avatar, email, name, username} = userInfo;

  return (
    <article className="post">
      <header className="post__header">
        <div className="user">
          <Link to={`/user/${username}`} className="user__thumb">
            <img src={avatar} alt={name} />
          </Link>
          <Link to={`/user/${username}`} className="user__name">
            {name}
          </Link>
        </div>
        <button onClick={() => toggleFolowing(!following)} className="post__context">
          <span className="follow-btn">Seguir</span>
          <span className={"follow-btn" + (!following ? "is-following" : "")}>
            {!following ? "Seguindo" : "Seguir"}
          </span>
        </button>
      </header>

    </article>
  );
};

export default Post;
